import { useState, useEffect } from "react";
import { makeStyles, withStyles } from "@material-ui/core/styles";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Typography from "@material-ui/core/Typography";
import Container from "@material-ui/core/Container";
import React from "react";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import { Paper, Grid, TableContainer, Table, TableBody, TableHead, TableRow, TableCell, Button, CircularProgress } from "@material-ui/core";
import image1 from "./bg.png";
import image from "./BLkE.gif";
import image2 from "./IrM.gif";
import { DropzoneArea } from 'material-ui-dropzone';
import { common } from '@material-ui/core/colors';
import Clear from '@material-ui/icons/Clear';
import ReactAudioPlayer from 'react-audio-player';


const ColorButton = withStyles((theme) => ({
  root: {
    color: theme.palette.getContrastText(common.white),
    backgroundColor: common.white,
    '&:hover': {
      backgroundColor: '#ffffff7a',
    },
  },
}))(Button);
const axios = require("axios").default;

const useStyles = makeStyles((theme) => ({
  grow: {
    flexGrow: 1,
  },
  clearButton: {
    width: "-webkit-fill-available",
    borderRadius: "15px",
    padding: "15px 22px",
    color: "#000000a6",
    fontSize: "20px",
    fontWeight: 900,
  },
  root: {
    maxWidth: 345,
    flexGrow: 1,
  },
  Typography: {
    fontFamily: "Bebas Neue",
    fontStyle: 'bold',
  },
  Typography1: {
    fontStyle: 'italic',
    color: 'white',
  },
  tool: {
    backgroundImage: `url(${image2})`,
    justifyContent:"center",
    display:"flex",
  },
  tool1: {
    backgroundImage: `url(${image1})`,
    justifyContent:"center",
    display:"flex",
  },
  paper: {
    padding: theme.spacing(2),
    margin: 'auto',
    maxWidth: 500,
  },
  gridContainer: {
    justifyContent: "center",
    padding: "4em 1em 0 1em",
  },
  mainContainer: {
    backgroundImage: `url(${image})`,
    backgroundRepeat: 'no-repeat',
    backgroundPosition: 'center',
    backgroundSize: 'cover',
    height: "93vh",
    marginTop: "8px",
  },
  imageCard: {
    margin: "auto",
    maxWidth: 400,
    height: 270,
    backgroundColor: 'transparent',
    boxShadow: '0px 9px 70px 0px rgb(0 0 0 / 30%) !important',
    borderRadius: '15px',
  },
  imageCardEmpty: {
    height: 'auto',
  },
  noImage: {
    margin: "auto",
    width: 400,
    height: "400 !important",
  },
  input: {
    display: 'none',
  },
  uploadIcon: {
    background: 'white',
  },
  tableContainer: {
    backgroundColor: 'transparent !important',
    boxShadow: 'none !important',
  },
  table: {
    backgroundColor: 'transparent !important',
  },
  tableHead: {
    backgroundColor: 'transparent !important',
  },
  tableRow: {
    backgroundColor: 'transparent !important',
  },
  tableCell: {
    fontSize: '22px',
    backgroundColor: 'transparent !important',
    borderColor: 'transparent !important',
    color: '#000000a6 !important',
    fontWeight: 'bolder',
    padding: '1px 24px 1px 16px',
  },
  tableCell1: {
    fontSize: '14px',
    backgroundColor: 'transparent !important',
    borderColor: 'transparent !important',
    color: '#000000a6 !important',
    fontWeight: 'bolder',
    padding: '1px 24px 1px 16px',
  },
  tableBody: {
    backgroundColor: 'transparent !important',
  },
  text: {
    color: 'white !important',
    textAlign: 'center',
  },
  buttonGrid: {
    maxWidth: "216px",
    width: "100%",
  }, 
  detail: {
    backgroundColor: 'white',
    display: 'flex',
    justifyContent: 'center',
    flexDirection: 'column',
    alignItems: 'center',
  },
  appbar: {
    background: `url(${image1})`,
  },
  loader: {
    color: '#be6a77 !important',
  }
}));


export const AudioUpload = () => {
  const classes = useStyles();
  const [preview, setPreview] = useState();
  const [data, setData] = useState();
  const [image, setImage] = useState(false);
  const [isLoading, setIsloading] = useState(false);
  const [audio, setAudio] = useState();
  let confidence_g = 0;
  let confidence_e = 0;
  let confidence_a = 0;

  const sendFile = async () => {
    if (image) {
      let formData = new FormData();
      formData.append("file", audio);
      let res = await axios({
        method: "post",
        url: process.env.REACT_APP_API_URL,
        data: formData,
      });
      if (res.status === 200) {
        setData(res.data);
      }
      setIsloading(false);
    }
  }

  const clearData = () => {
    setData(null);
    setImage(false);
    setAudio(null);
    setPreview(null);
  };

  useEffect(() => {
    if (!audio) {
      setPreview(undefined);
      return;
    }
    const objectUrl = URL.createObjectURL(audio)
    setPreview(objectUrl) 
  }, [audio]);

  useEffect(() => {
    if (!preview) {
      return;
    }
    setIsloading(true);
    sendFile();
  }, [preview]);

  const addFile = (files) => {
    if (!files || files.length === 0) {
      setAudio(undefined);
      setImage(false);
      setData(undefined);
      return;
    }
    if (files[0]) {
      setAudio(files[0]);
      setData(undefined);
      setImage(true);
    }
  };

  if (data) {
    confidence_g = (parseFloat(data.confidence_g) * 100).toFixed(2);
    confidence_e = (parseFloat(data.confidence_e) * 100).toFixed(2);
    confidence_a = (parseFloat(data.confidence_a) * 100).toFixed(2);
  }
  

  return (
    <React.Fragment>
      <AppBar position="static" className={classes.appbar}>
        <Toolbar className={classes.tool1}>
          <Typography className={classes.Typography} align="center"  variant ='h5' >
            AUDIO ANALYTICS: DETERMINE GENDER, AGE AND EMOTION
          </Typography>
        </Toolbar>
      </AppBar>
      <Toolbar className={classes.tool}>
        <Typography className={classes.Typography1} align = "center"  >
          This Model will determine the gender, age and emotion of a person by analysing the audio.
          The audio file should be in .wav format.   
        </Typography>
      </Toolbar>
      <Container maxWidth={false} className={classes.mainContainer} disableGutters={true}>
        <Grid
          className={classes.gridContainer}
          container
          direction="row"
          justifyContent="center"
          alignItems="center"
          spacing={2}
        >
          <Grid item xs={12}>
            <Card className={`${classes.imageCard} ${!image ? classes.imageCardEmpty : ''}`}>
              {!image && <CardContent className={classes.content}>
              <DropzoneArea
                acceptedFiles={['audio/*']}
                dropzoneText={"Drag and drop an audio file to process"}
                onChange={addFile}
              />
              </CardContent>}
              {data  && <CardContent className={classes.detail}>
                <TableContainer component={Paper} className={classes.tableContainer}>
                  <TableRow align = "center" className={classes.ReactAudioPlayer}>
                    <ReactAudioPlayer 
                      src = {URL.createObjectURL(audio)}
                      autoPlay
                      controls
                    />
                  </TableRow>
                  <Table className={classes.table} size="small" aria-label="simple table">
                    <TableHead className={classes.tableHead}>
                      <TableRow className={classes.tableRow}>
                        <TableCell className={classes.tableCell1}>Gender:</TableCell>
                        <TableCell align="right" className={classes.tableCell1}>Confidence:</TableCell>
                      </TableRow>                     
                    </TableHead>
                    <TableBody className={classes.tableBody}>
                      <TableRow className={classes.tableRow}>
                        <TableCell component="th" scope="row" className={classes.tableCell}>
                          {data.Gender}
                        </TableCell>
                        <TableCell align="right" className={classes.tableCell}>{confidence_g}%</TableCell>        
                      </TableRow>            
                    </TableBody>
                    <TableHead className={classes.tableHead}>
                      <TableRow className={classes.tableRow}>
                        <TableCell className={classes.tableCell1}>Emotion:</TableCell>
                        <TableCell align="right" className={classes.tableCell1}>Confidence:</TableCell>
                      </TableRow>                     
                    </TableHead>
                    <TableBody className={classes.tableBody}>
                      <TableRow className={classes.tableRow}>
                        <TableCell component="th" scope="row" className={classes.tableCell}>
                          {data.Emotion}
                        </TableCell>
                        <TableCell align="right" className={classes.tableCell}>{confidence_e}%</TableCell>        
                      </TableRow>            
                    </TableBody>
                    <TableHead className={classes.tableHead}>
                      <TableRow className={classes.tableRow}>
                        <TableCell className={classes.tableCell1}>Age:</TableCell>
                        <TableCell align="right" className={classes.tableCell1}>Confidence:</TableCell>
                      </TableRow>                     
                    </TableHead>
                    <TableBody className={classes.tableBody}>
                      <TableRow className={classes.tableRow}>
                        <TableCell component="th" scope="row" className={classes.tableCell}>
                          {data.AGE}
                        </TableCell>
                        <TableCell align="right" className={classes.tableCell}>{confidence_a}%</TableCell>        
                      </TableRow>            
                    </TableBody>
                  </Table>
                </TableContainer>
              </CardContent>}
              {isLoading && <CardContent className={classes.detail}>
                <CircularProgress color="secondary" className={classes.loader} />
                <Typography className={classes.title} variant="h6" noWrap>
                  Processing
                </Typography>
              </CardContent>}
            </Card>
          </Grid>
          {data &&
            <Grid item className={classes.buttonGrid} >
              <ColorButton variant="contained" className={classes.clearButton} color="primary" component="span" size="large" onClick={clearData} startIcon={<Clear fontSize="large" />}>
                Clear
              </ColorButton>
            </Grid>}
        </Grid >
      </Container >
    </React.Fragment >
  );
};
